/* Global Design System - Professional & Cohesive Styling */
/* Technology: Pure Bootstrap 5.3+ */

/* Import Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@600;700&family=Roboto:wght@400&display=swap');

/* Color Palette Variables - Bootstrap 5.3+ Compliant */
:root {
  /* Bootstrap Variable Overrides - The Right Way */
  --bs-primary: #0D6EFD;
  --bs-primary-rgb: 13, 110, 253;
  --bs-light: #F8F9FA;
  --bs-dark: #212529;
  --bs-body-color: #495057;
  --bs-body-bg: #FFFFFF;

  /* Custom Design System Variables - Alias to Bootstrap tokens for consistency */
  --primary-blue: var(--bs-primary);
  --dark-charcoal: var(--bs-dark);
  --muted-gray: var(--bs-body-color);
  --light-gray: var(--bs-light);
  --white: var(--bs-body-bg);

  /* Shadow System */
  --shadow-sm: 0 .125rem .25rem rgba(0,0,0,.075);
  --shadow-hover: 0 .5rem 1rem rgba(0,0,0,.15);

  /* Transitions */
  --transition-base: all 0.3s ease-in-out;
}

/* Typography System */
body {
  font-family: 'Roboto', -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: var(--muted-gray);
  background-color: var(--white);
}

/* Headings Typography - All headings use Poppins as per spec */
h1, h2, h3, h4, h5, h6 {
  font-family: 'Poppins', -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
  color: var(--dark-charcoal);
  line-height: 1.2;
  margin-bottom: 1rem;
}

h1 {
  font-weight: 700;
}

h2, h3, h4, h5, h6 {
  font-weight: 600;
}

/* Body Text */
p, li, span {
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  color: var(--muted-gray);
}

/* Lead Text */
.lead {
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  font-size: 1.25rem;
  color: var(--muted-gray);
}

/* Button System */
.btn {
  border-radius: 50rem !important; /* .rounded-pill */
  padding: 12px 28px;
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  font-size: 1rem;
  transition: var(--transition-base);
  border-width: 2px;
}

/* Primary CTA Buttons - Solid Blue */
.btn-primary {
  background-color: var(--bs-primary);
  border-color: var(--bs-primary);
  color: var(--bs-white);
}

.btn-primary:hover,
.btn-primary:focus {
  background-color: var(--bs-primary);
  border-color: var(--bs-primary);
  color: var(--bs-white);
  box-shadow: var(--shadow-hover);
  transform: translateY(-2px);
}

/* Secondary Buttons - Outline */
.btn-outline-primary {
  color: var(--bs-primary);
  border-color: var(--bs-primary);
  background-color: transparent;
}

.btn-outline-primary:hover,
.btn-outline-primary:focus {
  background-color: var(--bs-primary);
  border-color: var(--bs-primary);
  color: var(--bs-white);
  box-shadow: var(--shadow-hover);
  transform: translateY(-2px);
}

/* Light/White Buttons */
.btn-light {
  background-color: var(--white);
  border-color: var(--white);
  color: var(--primary-blue);
}

.btn-light:hover,
.btn-light:focus {
  background-color: var(--bs-white);
  border-color: var(--bs-white);
  color: var(--bs-primary);
  box-shadow: var(--shadow-hover);
  transform: translateY(-2px);
}

/* Button Sizes */
.btn-lg {
  padding: 16px 32px;
  font-size: 1.125rem;
}

.btn-sm {
  padding: 8px 20px;
  font-size: 0.875rem;
}

/* Card System */
.card {
  border: 1px solid rgba(0,0,0,.125);
  border-radius: 0.375rem;
  box-shadow: var(--shadow-sm);
  transition: var(--transition-base);
  background-color: var(--white);
}

.card:hover {
  transform: translateY(-4px);
  box-shadow: var(--shadow-hover);
}

.card-body {
  padding: 1.5rem;
}

/* Custom Spacing Classes - Don't override Bootstrap utilities */
section {
  padding: 5rem 0; /* Generous section spacing */
}

/* Custom spacing utilities instead of overriding Bootstrap */
.section-space {
  padding: 5rem 0;
}

.space-y-lg {
  margin-bottom: 5rem;
}

.space-y-md {
  margin-bottom: 3rem;
}

/* Custom Background Classes - Don't override Bootstrap utilities */
.hero-gradient {
  background: linear-gradient(135deg, var(--primary-blue), #0B5ED7) !important;
}

/* Custom Text Utilities - Use Bootstrap variables only */
/* Removed .text-* overrides for Bootstrap 5.3+ compliance */

/* Link Styling */
a {
  color: var(--bs-primary);
  text-decoration: none;
  transition: var(--transition-base);
}

a:hover {
  color: var(--bs-primary);
  opacity: 0.8;
}

/* Navigation Dropdowns */
.dropdown-menu {
  border: 1px solid rgba(0,0,0,.15);
  box-shadow: var(--shadow-hover);
  border-radius: 0.375rem;
}

.dropdown-item {
  font-family: 'Roboto', sans-serif;
  color: var(--muted-gray);
  padding: 0.75rem 1.5rem;
  transition: var(--transition-base);
}

.dropdown-item:hover {
  background-color: var(--light-gray);
  color: var(--primary-blue);
}

/* Form Controls */
.form-control {
  border: 2px solid #DEE2E6;
  border-radius: 0.375rem;
  padding: 12px 16px;
  font-family: 'Roboto', sans-serif;
  transition: var(--transition-base);
}

.form-control:focus {
  border-color: var(--primary-blue);
  box-shadow: 0 0 0 0.2rem rgba(var(--bs-primary-rgb), 0.25);
}

/* Icon Styling */
.bi {
  color: var(--bs-primary);
}

/* Display Utilities */
.display-1, .display-2, .display-3, .display-4, .display-5, .display-6 {
  font-family: 'Poppins', sans-serif;
  font-weight: 700;
  color: var(--dark-charcoal);
}

/* Utility Classes */
.fw-bold {
  font-weight: 700 !important;
}

.fw-medium {
  font-weight: 600 !important;
}

/* Responsive Design */
@media (max-width: 768px) {
  section {
    padding: 3rem 0;
  }

  .display-4 {
    font-size: 2.5rem;
  }

  .btn {
    padding: 10px 24px;
    font-size: 0.9rem;
  }

  .btn-lg {
    padding: 14px 28px;
    font-size: 1rem;
  }
}

@media (max-width: 576px) {
  section {
    padding: 2rem 0;
  }

  .display-4 {
    font-size: 2rem;
  }

  .btn {
    padding: 8px 20px;
    font-size: 0.875rem;
  }
}

/* Smooth Scrolling */
html {
  scroll-behavior: smooth;
}

/* Loading States */
.loading {
  opacity: 0.6;
  pointer-events: none;
}

/* Animation System */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.fade-in-up {
  animation: fadeInUp 0.6s ease-out;
}

/* Dark Theme Support */
[data-bs-theme="dark"] {
  --white: #212529;
  --light-gray: #2D3136;
  --muted-gray: #ADB5BD;
  --dark-charcoal: #F8F9FA;
}

[data-bs-theme="dark"] body {
  background-color: var(--white);
  color: var(--muted-gray);
}

[data-bs-theme="dark"] h1,
[data-bs-theme="dark"] h2,
[data-bs-theme="dark"] h3,
[data-bs-theme="dark"] h4,
[data-bs-theme="dark"] h5,
[data-bs-theme="dark"] h6 {
  color: var(--dark-charcoal);
}

[data-bs-theme="dark"] .card {
  background-color: var(--light-gray);
  border-color: #495057;
}

[data-bs-theme="dark"] .dropdown-menu {
  background-color: var(--light-gray);
  border-color: #495057;
}

[data-bs-theme="dark"] .dropdown-item {
  color: var(--muted-gray);
}

[data-bs-theme="dark"] .dropdown-item:hover {
  background-color: #495057;
  color: var(--primary-blue);
}

/* Service Cards Enhanced Hover Effect */
.service-card {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.service-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
}

/* Blue underline accent for IT services */
.text-primary .position-absolute {
  transition: width 0.3s ease;
}

/* Advantage Cards Enhanced Styling */
.advantage-card {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.advantage-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
}

/* Feature Icon Consistent Sizing */
.feature-icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 4rem;
  height: 4rem;
}

