/* Footer bottom section */
footer hr {
  border-color: rgba(255, 255, 255, 0.1);
}

footer .text-muted {
  color: rgba(255, 255, 255, 0.75) !important;
}

/* Ensure all footer text is visible */
footer h4, 
footer h5, 
footer h6 {
